import React, { useState } from "react";
function App() {
  const [message, setMessage] = useState("Hello Artem");

  function editMessage() {
    setMessage((message = "Hello Sasha"));
  }
  return (
    <div className="App">
      <h1>{message}</h1>
      <input type="text" />

      <button onClick={editMessage}>Click me</button>
    </div>
  );
}

export default App;
