import "./styles/App.css";
function App() {
  let [tasks, setTask] = useState([
    { id: 1, text: "Купить пиво" },
    { id: 2, text: "Купить молоко" },
  ]);
  let [inpValue, setInpValue] = useState("");

  let list = tasks.map((task) => {
    return <li key={task.id}>{task.text}</li>;
  });

  function addNewTask() {}

  return (
    <div className="app">
      <div>
        <form className="app__header">
          <input
            value={inpValue}
            onChange={(e) => setInpValue(e.target.value)}
          />
          <button type="submit" className="app__btn">
            Add
          </button>
        </form>
        <ul></ul>
        {list}
      </div>
    </div>
  );
}

export default App;
