import React, { useState } from "react";
import "./styles/App.css";
function App() {
  let [tasks, setTasks] = useState([
    { id: 1, text: "Купить пиво" },
    { id: 2, text: "Купить молоко" },
  ]);
  let [task, setTask] = useState("");

  function TasksList(props) {
    return (
      <div className="app__item">
        <li>{props.task.text}</li>
        <button>remove</button>
      </div>
    );
  }

  function addNewTask(e) {
    e.preventDefault();
    const newTask = { id: 3, text: "Купить креветки" };
    setTasks(tasks.push(newTask));
  }

  return (
    <div className="app">
      <div className="app__content">
        <form className="app__header" onSubmit={addNewTask}>
          <input value={task} onChange={(e) => setTask(e.target.value)} />
          <button type="submit" className="app__btn">
            Add
          </button>
        </form>
        <ul>
          {tasks.map((item) => {
            return <TasksList task={task} key={task.id} />;
          })}
        </ul>
      </div>
    </div>
  );
}

export default App;
